import { useEffect, useState } from "react";
import { getStudents, deleteStudent, updateStudent } from "../api";

const StudentTable = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const { data } = await getStudents();
    setStudents(data);
  };

  const handleDelete = async (id) => {
    await deleteStudent(id);
    fetchStudents();
  };

  const handleUpdate = async (id, field, value) => {
    const student = students.find((s) => s._id === id);
    student[field] = value;
    await updateStudent(id, student);
    fetchStudents();
  };

  return (
    <table className="w-full border-collapse border border-gray-200">
      <thead>
        <tr className="bg-gray-100">
          {["Prénom", "Nom", "Âge", "Adresse", "Email", "Licence", "Actions"].map((h) => (
            <th key={h} className="border p-2">{h}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {students.map((student) => (
          <tr key={student._id} className="border">
            {["firstName", "lastName", "age", "address", "email", "licence"].map((field) => (
              <td key={field} className="border p-2">
                <input
                  type="text"
                  value={student[field]}
                  onChange={(e) => handleUpdate(student._id, field, e.target.value)}
                  className="w-full border p-1"
                />
              </td>
            ))}
            <td className="border p-2">
              <button onClick={() => handleDelete(student._id)} className="bg-red-500 text-white px-2 py-1 rounded">
                Supprimer
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default StudentTable;
