import React, { useState, useEffect } from 'react';
import AddStudent from './AddStudent';
import './StudentTable.css';  // Import CSS if you are using a separate stylesheet

const StudentTable = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [isFormOpen, setIsFormOpen] = useState(false);

  // Fetch students from the server
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/students');
        const data = await response.json();
        setStudents(data);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };

    fetchStudents();
  }, []); // Fetch once when component is loaded

  // Handle editing a student
  const handleEdit = (student) => {
    setSelectedStudent(student); // Set the student to edit
    setIsFormOpen(true); // Open the form
  };

  // Handle deleting a student
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        // Send DELETE request to backend to delete the student
        const response = await fetch(`http://localhost:5000/api/students/${id}`, {
          method: 'DELETE',
        });

        if (response.ok) {
          // After deletion, remove the deleted student from the list
          setStudents((prevStudents) => prevStudents.filter((student) => student._id !== id));
        } else {
          console.error('Failed to delete student');
        }
      } catch (error) {
        console.error('Error deleting student:', error);
      }
    }
  };

  // Handle closing the form
  const handleCloseForm = () => {
    setSelectedStudent(null); // Reset selected student
    setIsFormOpen(false); // Close the form
  };

  // Handle saving a student (either new or updated)
  const handleSaveStudent = (student) => {
    setStudents((prevStudents) => {
      const updatedStudents = prevStudents.filter((s) => s._id !== student._id);
      return [...updatedStudents, student];
    });
  };

  return (
    <div>
      {/* Add button to open the form for adding a new student */}
      <button onClick={() => setIsFormOpen(true)} className="add-button">Add Student</button>

      {/* Conditional rendering of the Add/Edit form */}
      {isFormOpen && (
        <AddStudent
          studentToEdit={selectedStudent}
          onClose={handleCloseForm}
          onSave={handleSaveStudent}
        />
      )}

      {/* Table to display the list of students */}
      <table className="student-table">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id}>
              <td>{student.firstName}</td>
              <td>{student.lastName}</td>
              <td>{student.age}</td>
              <td>{student.email}</td>
              <td>
                {/* Edit and Delete buttons */}
                <button onClick={() => handleEdit(student)} className="edit-button">Edit</button>
                <button onClick={() => handleDelete(student._id)} className="delete-button">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentTable;
