import React from "react";
import { Link } from "react-router-dom";

const StudentTable = ({ students }) => {

   // Handle editing a student
   const handleEdit = (student) => {
    setSelectedStudent(student); // Set the student to edit
    setIsFormOpen(true); // Open the form
  };

  // Handle deleting a student
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        // Send DELETE request to backend to delete the student
        const response = await fetch(`http://localhost:5000/api/students/${id}`, {
          method: 'DELETE',
        });

        if (response.ok) {
          // After deletion, remove the deleted student from the list
          setStudents((prevStudents) => prevStudents.filter((student) => student._id !== id));
        } else {
          console.error('Failed to delete student');
        }
      } catch (error) {
        console.error('Error deleting student:', error);
      }
    }
  };

  // Handle closing the form
  const handleCloseForm = () => {
    setSelectedStudent(null); // Reset selected student
    setIsFormOpen(false); // Close the form
  };

  // Handle saving a student (either new or updated)
  const handleSaveStudent = (student) => {
    setStudents((prevStudents) => {
      const updatedStudents = prevStudents.filter((s) => s._id !== student._id);
      return [...updatedStudents, student];
    });
  };


  return (
    <div className="container mx-auto p-4">
      {/* Add Student Button */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-bold">Student List</h2>
        <Link to="/add-student">
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700">
            Add Student
          </button>
        </Link>
      </div>

      {/* Student Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border px-4 py-2">First Name</th>
            <th className="border px-4 py-2">Last Name</th>
            <th className="border px-4 py-2">Age</th>
            <th className="border px-4 py-2">Address</th>
            <th className="border px-4 py-2">Email</th>
            <th className="border px-4 py-2">Licence</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id} className="text-center">
              <td className="border px-4 py-2">{student.firstName}</td>
              <td className="border px-4 py-2">{student.lastName}</td>
              <td className="border px-4 py-2">{student.age}</td>
              <td className="border px-4 py-2">{student.address}</td>
              <td className="border px-4 py-2">{student.email}</td>
              <td className="border px-4 py-2">{student.licence}</td>
              <td>
                  <button onClick={() => handleEdit(student)}>Edit</button>
                  <button onClick={() => handleDelete(student._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentTable;
