import React, { useState, useEffect } from 'react';

const AddStudent = ({ studentToEdit, onClose, onSave }) => {
  const [student, setStudent] = useState({
    firstName: '',
    lastName: '',
    age: '',
    email: '',
    licene: '',
  });

  useEffect(() => {
    if (studentToEdit) {
      setStudent(studentToEdit);  // Pre-fill the form with student data if editing
    }
  }, [studentToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // If there's an existing student to edit, update them
    if (student._id) {
      onSave(student);
    } else {
      // If it's a new student, create them (POST)
      onSave({ ...student, _id: Date.now().toString() });
    }

    onClose();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          value={student.firstName}
          onChange={handleChange}
          placeholder="First Name"
        />
        <input
          type="text"
          name="lastName"
          value={student.lastName}
          onChange={handleChange}
          placeholder="Last Name"
        />
        <input
          type="number"
          name="age"
          value={student.age}
          onChange={handleChange}
          placeholder="Age"
        />
        <input
          type="email"
          name="email"
          value={student.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="text"
          name="license"
          value={student.license}
          onChange={handleChange}
          placeholder="License"
        />
        <button type="submit">{student._id ? 'Update Student' : 'Add Student'}</button>
      </form>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
};

export default AddStudent;
